// Filter Location Data - validates data and determines if it should be processed
const item = $input.first();
console.log("FULL INPUT:", JSON.stringify(item, null, 2));

try {
  // Try to find the data in various possible locations
  let inputData = null;

  if (item.json && item.json.body) {
    console.log("Found item.json.body structure");
    inputData = item.json.body;
  } else if (item.body) {
    console.log("Found item.body structure");
    inputData = item.body;
  } else if (item.lat !== undefined && item.lon !== undefined) {
    console.log("Found direct lat/lon in item");
    inputData = item;
  } else if (item.json && item.json.lat !== undefined && item.json.lon !== undefined) {
    console.log("Found lat/lon in item.json");
    inputData = item.json;
  } else {
    // More aggressive search
    const itemStr = JSON.stringify(item);
    console.log("Searching for coordinates in entire item: " + itemStr.substring(0, 200) + "...");

    // Try to find any object with lat/lon in the entire item
    const findCoords = (obj, path = "") => {
      if (!obj || typeof obj !== 'object') return null;

      if (obj.lat !== undefined && obj.lon !== undefined) {
        console.log(`Found coordinates at path: ${path}`);
        return obj;
      }

      for (const key in obj) {
        if (typeof obj[key] === 'object') {
          const result = findCoords(obj[key], `${path}.${key}`);
          if (result) return result;
        }
      }

      return null;
    };

    inputData = findCoords(item);
  }

  console.log("InputData found:", inputData ? JSON.stringify(inputData, null, 2) : "NULL");

  // Validate if we found data with coordinates
  if (inputData && inputData.lat !== undefined && inputData.lon !== undefined) {
    console.log(`Valid coordinates found: ${inputData.lat}, ${inputData.lon}`);

    // Create a properly structured output
    return [{
      json: {
        source: 'owntracks',
        type: inputData._type || 'location',
        coordinates: {
          latitude: inputData.lat,
          longitude: inputData.lon,
          accuracy: inputData.acc || 0
        },
        device: {
          battery: inputData.batt || 0,
          trigger: inputData.t || '',
          timestamp: inputData.tst || 0
        },
        raw: inputData,
        valid: true,
        skip: false
      }
    }];
  } else {
    // Log the failure and return with skip flag
    console.log("Failed to find valid coordinates in the input");
    return [{
      json: {
        valid: false,
        skip: true,
        raw: item,
        reason: 'Missing latitude or longitude'
      }
    }];
  }
} catch (error) {
  // Handle any errors
  console.error('Error processing Owntracks data:', error);
  return [{
    json: {
      valid: false,
      skip: true,
      error: error.message,
      raw: item
    }
  }];
}

// Success handler
const item = $input.item.json;

console.log('✅ Successfully sent to Poke');
console.log('Response:', JSON.stringify(item, null, 2));

return {
  json: {
    success: true,
    status: item.statusCode || 200,
    message: 'Location update sent to Poke',
    timestamp: new Date().toISOString()
  }
};

// Skipped handler
const item = $input.item.json;

console.log('⏭️  Location update skipped');
console.log('Reason:', item.reason || 'No reason provided');

return {
  json: {
    skipped: true,
    reason: item.reason,
    timestamp: new Date().toISOString()
  }
};

// Error handler
const error = $input.item.json;

console.error('❌ Error processing location update');
console.error('Error:', JSON.stringify(error, null, 2));

// Return error details
return {
  json: {
    error: true,
    message: error.message || 'Unknown error',
    details: error,
    timestamp: new Date().toISOString()
  }
};

